document.addEventListener('DOMContentLoaded',function(){const dom=document.querySelector('[data-echarts-map="china"]');if(!dom)return;const chart=echarts.init(dom);fetch('/_javascripts/china.json').then(res=>res.json()).then(geoJson=>{echarts.registerMap('china',geoJson);chart.setOption({tooltip:{trigger:'item',formatter:'{b}'},series:[{name:'中国地图',type:'map',map:'china',roam:false,selectedMode:'single',label:{show:false},itemStyle:{normal:{areaColor:'#ccc',borderColor:'#fff',borderWidth:1},emphasis:{areaColor:'#999'}},data:[{name:'辽宁',selected:false,itemStyle:{normal:{areaColor:'#1890ff'},emphasis:{areaColor:'#40a9ff'}}}
]}]});chart.on('click',params=>{console.log('当前选中：',params.name);});}).catch(err=>console.error('地图加载失败:',err));});