document.addEventListener('DOMContentLoaded',function(){var dom=document.querySelector('[data-echarts-map="china"]');if(!dom)return;var chart=echarts.init(dom);fetch('/_javascripts/china.json').then(res=>res.json()).then(geoJson=>{echarts.registerMap('china',geoJson);chart.setOption({tooltip:{trigger:'item',formatter:'{b}'},series:[{name:'中国',type:'map',map:'china',roam:false,label:{show:false,},itemStyle:{normal:{areaColor:'#ccc',borderColor:'#fff',borderWidth:1},emphasis:{areaColor:'#999'}},data:[{name:'辽宁',value:0,itemStyle:{normal:{areaColor:'#1890ff'},emphasis:{areaColor:'#40a9ff'}}}
]}]});}).catch(err=>console.error(err));});