document.addEventListener('DOMContentLoaded',function(){document.querySelectorAll('[data-echarts-map="china"]').forEach(function(container){var chart=echarts.init(container);fetch('/_javascripts/china.json').then(res=>res.json()).then(geoJson=>{echarts.registerMap('china',geoJson);chart.setOption({tooltip:{trigger:'item',formatter:'{b}'},series:[{type:'map',map:'china',roam:false,emphasis:{itemStyle:{areaColor:'#a0d911'}},data:[{name:'河北',value:1,itemStyle:{color:'#1890ff'}}]}]});});});});